var _stylesheet = {
	/**
	 * The ID of the style element that we use for our rules
     */
    styleElementId: null,

    /** 
     * Get the single style element, creating if required
     */
    getStyleElementId: function () {
        if (_stylesheet.styleElementId == null) {
            // Create the <style> tag
            var style = document.createElement("style");

            style.type = "text/css";
            style.id = _stringUtils.createUUID({ beginWithLetter: true });

            const defaultRules = `
                @keyframes fontbulger {
                    0% { transform: scale(1); }
                    100% { transform: scale(1.2); }
                }
            `

            style.appendChild(document.createTextNode(defaultRules))

            // Add the <style> element to the page
            document.head.appendChild(style);

            _stylesheet.styleElementId = style.id;
        }

        return _stylesheet.styleElementId;
    },

    /**
     * Get the rules selector for the rules of a specific class. This uses
     * a filtered selector so the style element is the one we can reference
     */
    getRulesSelectorForClassName: function (className) {
        var id = _stylesheet.getStyleElementId();

        return "#" + id + " {." + className + "}";
    },

    /**
     * get the combined CSSText for every rule of the style sheet
     */
    getCSSTextForStyleElement: function () {
        var id = _stylesheet.getStyleElementId();
        var styleElement = document.getElementById(id);

        var rules = styleElement.sheet.cssRules;
        var cssText = "";

        for (var i = 0; i < rules.length; i++) {
            cssText += rules[i].cssText;
            cssText += "\n\n";
        }

        return cssText;
    },

    /**
	 * get *every* rule for *every* class of our style element as text,
	 * and apply to the style element's innerText property. This should
	 * evaluate to the same thing, but allow the element to be saved
	 */
    updateInnerTextForHighlightStyleElement: function () {
        "use strict";
        var id = _stylesheet.getStyleElementId();
        var styleElement = document.getElementById(id);

        if (styleElement) {
            styleElement.innerHTML = _stylesheet.getCSSTextForStyleElement();
        }
    },

    setHighlightStyle: function (dfn) {
        "use strict"
        // new style (not a CSSRule)
        var style = Object.assign({}, dfn.style)

        // account for styles defined before box-shadow was defined
        const backgroundColor = dfn.style['background-color'];
        
        if (!dfn.disableBoxShadow) {
            style["box-shadow"] = "0 0 12px " + backgroundColor;
        }

        if (dfn.inherit_style_color) {
            style.color = "inherit";
        }

        const re = new RegExp("^#([0-9A-F]{2})([0-9A-F]{2})([0-9A-F]{2})", "ig");
        const match = re.exec(backgroundColor);

        if (match && match.length >= 4) {
            return _storage.getValue("highlightBackgroundAlpha").then(a => {
                style["background-color"] = "rgba(" +
                    parseInt(match[1], 16) + ", " +
                    parseInt(match[2], 16) + ", " +
                    parseInt(match[3], 16) + ", " +
                    (a || 1.0) +
                ")";

                // shared CSSStyleSheet containing all styles
                const sheet = document.getElementById(_stylesheet.getStyleElementId()).sheet
                const selectorText = `.${dfn.className}`
                
                // delete existing rule in sheet (if possible)
                const ruleIndex = _stylesheet.clearHighlightStyle(dfn.className)
                // for (var ruleIndex=0; ruleIndex < sheet.cssRules.length; ruleIndex++) {
                //     const cssRule = sheet.cssRules[ruleIndex]

                //     if (cssRule.type === CSSRule.STYLE_RULE && cssRule.selectorText === selectorText) {
                //         sheet.deleteRule(ruleIndex)
                //         break
                //     }
                // }

                const rule = `${selectorText}
                    ${JSON.stringify(style, null, '\t')
                        .replace(/"/g,'')
                        .replace(/,\n/g,';')
                        .replace(/\}/g, ';}')
                    }
                `
                // try to insert replacement rule at same index
                sheet.insertRule(rule, ruleIndex == -1 ? sheet.cssRules.length : ruleIndex)// Math.min(ruleIndex, sheet.cssRules.length))
            });
        } else {
            console.log("highlight style background colour not in #RRGGBB format");
            return Promise.reject(new Error());
        }
    },


    /**
     * Remove rules for a single style
     * @param className
     */
    clearHighlightStyle: function (className) {
        // shared CSSStyleSheet containing all styles
        const sheet = document.getElementById(_stylesheet.getStyleElementId()).sheet
        const selectorText = `.${className}`

        // delete existing rule in sheet (if possible)
        for (var ruleIndex=0; ruleIndex < sheet.cssRules.length; ruleIndex++) {
            const cssRule = sheet.cssRules[ruleIndex]

            if (cssRule.type === CSSRule.STYLE_RULE && cssRule.selectorText === selectorText) {
                sheet.deleteRule(ruleIndex)
                return ruleIndex
            }
        }

        return -1
    },

    /**
     * Set the style rules used for the close button
     */
    setCloseButtonStyle: function (className) {
        // shared CSSStyleSheet containing all styles
        const sheet = document.getElementById(_stylesheet.getStyleElementId()).sheet

        sheet.insertRule(`
            .${className} .close {
                position: absolute;
                opacity: 0;
                transform: scale(0.6);
                left: -8px;
                top: -8px;
                width: 16px;
                height: 16px;
                cursor: pointer;
                z-index: 1000;
                transition: opacity 275ms, transform 275ms cubic-bezier(0.175, 0.885, 0.32, 1.275), background 175ms;
                background: url(data:image/png;base64,) no-repeat;
                background-origin: border-box;
            }
        `)

        sheet.insertRule(`
            .${className} .close:hover,
            .${className} .close:focus {
                background-image: url(data:image/png;base64,);
        }`)
    },
};
